import { useState } from "react";

// Mock logo data for providers - using simple colored placeholders
const mockProviderLogos: Record<number, string> = {
  1: "data:image/svg+xml;base64,",
  2: "data:image/svg+xml;base64,",
  3: "data:image/svg+xml;base64,",
  4: "data:image/svg+xml;base64,",
  5: "data:image/svg+xml;base64,",
  6: "data:image/svg+xml;base64,",
  7: "data:image/svg+xml;base64,",
  8: "data:image/svg+xml;base64,"
};

interface ProviderLogoProps {
  providerId: number;
  size?: "sm" | "md" | "lg" | "xl";
  className?: string;
  providerName?: string;
}

export function ProviderLogo({ providerId, size = "md", className = "", providerName }: ProviderLogoProps) {
  const [logoError, setLogoError] = useState(false);
  
  // Get logo URL directly from mock data
  const logoUrl = mockProviderLogos[providerId];

  const sizeClasses = {
    sm: "w-6 h-6",
    md: "w-8 h-8", 
    lg: "w-10 h-10",
    xl: "w-12 h-12"
  };

  // Get provider initials from name
  const getInitials = (name?: string) => {
    if (!name) return "P";
    return name.split(" ").map(word => word[0]).join("").toUpperCase().slice(0, 2);
  };

  if (logoUrl && !logoError) {
    return (
      <div className={`${sizeClasses[size]} rounded-md overflow-hidden bg-white/5 flex items-center justify-center ${className}`}>
        <img 
          src={logoUrl} 
          alt="Provider Logo"
          className="w-full h-full object-contain"
          onError={() => setLogoError(true)}
        />
      </div>
    );
  }

  // Fallback to provider initials
  return (
    <div className={`${sizeClasses[size]} rounded-md bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center ${className}`}>
      <span className={`font-semibold text-white ${size === 'sm' ? 'text-xs' : size === 'md' ? 'text-xs' : size === 'lg' ? 'text-sm' : 'text-base'}`}>
        {getInitials(providerName)}
      </span>
    </div>
  );
}